import React, { useState, useEffect } from 'react';
import { useForm, useFieldArray } from 'react-hook-form';
import { supabase } from './supabase';
import './form.css';

const ProductForm = () => {
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [newCategory, setNewCategory] = useState('');
  const [newSubcategory, setNewSubcategory] = useState('');

  const { register, handleSubmit, control, formState: { errors }, watch, setValue } = useForm({
    defaultValues: {
      title: '',
      details: '',
      price: '',
      image: null,
      category: '',
      subcategory: '',
      addons: [{ image: null, title: '', price: '' }]
    }
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: 'addons'
  });

  const selectedCategory = watch('category');

  useEffect(() => {
    // Fetch categories from the database on component mount
    const fetchCategories = async () => {
      const { data, error } = await supabase.from('categories').select('*');
      if (error) {
        console.error(error);
      } else {
        setCategories(data);
      }
    };

    fetchCategories();
  }, []);

  const addNewCategory = async () => {
    if (newCategory && !categories.find(cat => cat.name === newCategory)) {
      const { data, error } = await supabase.from('categories').insert({ name: newCategory }).select();
      if (error) {
        console.error(error);
      } else {
        setCategories([...categories, { name: newCategory, subcategories: [] }]);
        setNewCategory('');
        setSubcategories([]);
        setNewSubcategory('');
        setValue('category', newCategory);
      }
    }
  };

  const addNewSubcategory = async () => {
    if (newSubcategory && selectedCategory) {
      const { data, error } = await supabase.from('subcategories').insert({ name: newSubcategory, category: selectedCategory }).select();
      if (error) {
        console.error(error);
      } else {
        const updatedCategories = categories.map(cat => 
          cat.name === selectedCategory 
            ? { ...cat, subcategories: [...cat.subcategories, newSubcategory] }
            : cat
        );
        setCategories(updatedCategories);
        setSubcategories(updatedCategories.find(cat => cat.name === selectedCategory).subcategories);
        setNewSubcategory('');
      }
    }
  };

  const onSubmit = async (data) => {
    console.log(data);
    const { data: ProductImage, error: ProductImageError } = await supabase.storage
      .from("addon")
      .upload(`${data.title}-${data.details}`, data.image[0]);
    if (ProductImageError) {
      console.log(ProductImageError);
      return;
    }

    const { data: Product, error } = await supabase
      .from("products")
      .insert({
        title: data.title,
        details: data.details,
        price: data.price,
        image: ProductImage.path,
        category: data.category,
        subcategory: data.subcategory || null,
      })
      .select()
      .single();

    if (error) {
      console.log(error);
      return;
    }

    for (let addon of data.addons) {
      const { data: AddonFile, error: AddonFileError } = await supabase.storage
        .from("addon")
        .upload(`${Product.id}-${addon.title}`, addon.image[0]);
      if (AddonFileError) {
        console.error(AddonFileError);
        await supabase.from("products").delete().eq("id", Product.id);
        break;
      }
      const { error: AddonError } = await supabase.from("addons").insert({
        title: addon.title,
        price: addon.price,
        image: AddonFile.path,
        productid: Product.id
      });
      if (AddonError) {
        console.error(AddonError);
        await supabase.from("products").delete().eq("id", Product.id);
        break;
      }
    }

    window.location.reload();
  };

  return (
    <form className="" onSubmit={handleSubmit(onSubmit)}>
      <div>
        <label>Category:</label>
        <select 
          {...register('category', { required: 'Category is required' })}
          onChange={(e) => {
            const category = e.target.value;
            const selectedCatObj = categories.find(cat => cat.name === category);
            setSubcategories(selectedCatObj?.subcategories || []);
          }}
        >
          <option value="">Select Category</option>
          {categories.map((category, index) => (
            <option key={index} value={category.name}>
              {category.name}
            </option>
          ))}
        </select>
        {errors.category && <p>{errors.category.message}</p>}
      </div>

      <div>
        <input
          type="text"
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
          placeholder="New Category"
        />
        <button type="button" onClick={addNewCategory}>Add Category</button>
      </div>

      {subcategories.length > 0 && (
        <div>
          <label>Subcategory:</label>
          <select {...register('subcategory', { required: 'Subcategory is required' })}>
            <option value="">Select Subcategory</option>
            {subcategories.map((subcategory, index) => (
              <option key={index} value={subcategory}>
                {subcategory}
              </option>
            ))}
          </select>
          {errors.subcategory && <p>{errors.subcategory.message}</p>}
        </div>
      )}

      {selectedCategory && (
        <div>
          <input
            type="text"
            value={newSubcategory}
            onChange={(e) => setNewSubcategory(e.target.value)}
            placeholder="New Subcategory"
          />
          <button type="button" onClick={addNewSubcategory}>Add Subcategory</button>
        </div>
      )}

      <div>
        <label>Title:</label>
        <input
          type="text"
          {...register('title', { required: 'Title is required' })}
        />
        {errors.title && <p>{errors.title.message}</p>}
      </div>

      <div>
        <label>Details:</label>
        <textarea
          {...register('details', { required: 'Details are required' })}
        />
        {errors.details && <p>{errors.details.message}</p>}
      </div>

      <div>
        <label>Price:</label>
        <input
          type="number"
          {...register('price', { required: 'Price is required' })}
        />
        {errors.price && <p>{errors.price.message}</p>}
      </div>

      <div>
        <label>Image:</label>
        <input
          type="file"
          {...register('image', { required: 'Image is required' })}
        />
      </div>

      <div>
        <h3>Add-ons</h3>
        {fields.map((addon, index) => (
          <div key={addon.id}>
            <label>Image:</label>
            <input
              type="file"
              {...register(`addons.${index}.image`, { required: 'Image is required' })}
            />
            {errors.addons?.[index]?.image && <p>{errors.addons[index].image.message}</p>}

            <label>Title:</label>
            <input
              type="text"
              {...register(`addons.${index}.title`, { required: 'Addon title is required' })}
            />
            {errors.addons?.[index]?.title && <p>{errors.addons[index].title.message}</p>}

            <label>Price:</label>
            <input
              type="number"
              {...register(`addons.${index}.price`, { required: 'Addon price is required' })}
            />
            {errors.addons?.[index]?.price && <p>{errors.addons[index].price.message}</p>}

            <button type="button" onClick={() => remove(index)}>
              Remove Addon
            </button>
          </div>
        ))}

        <button
          type="button"
          onClick={() => append({ image: null, title: '', price: '' })}
        >
          Add Addon
        </button>
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default ProductForm;
