import React, { useState, useMemo, useEffect } from 'react';
import './boq.css';
import { Slider } from '@mui/material';
import ProductForm from './form';
import { supabase } from './supabase';

const Card = ({ title, price, image, details, addOns, initialMinimized = false }) => {
  const [selectedAddOns, setSelectedAddOns] = useState({});
  const [isMinimized, setIsMinimized] = useState(initialMinimized);
  const basePrice = price;

  const handleAddOnChange = (addOn, isChecked) => {
    setSelectedAddOns((prevSelectedAddOns) => ({
      ...prevSelectedAddOns,
      [addOn.name]: isChecked ? addOn.price : 0,
    }));
  };

  const calculateTotalPrice = useMemo(() => {
    return Object.values(selectedAddOns).reduce((total, addOnPrice) => total + addOnPrice, basePrice);
  }, [selectedAddOns, basePrice]);

  const toggleMinimize = () => setIsMinimized((prev) => !prev);

  if (isMinimized) {
    return (
      <div className="minimized-card" onClick={toggleMinimize}>
        <span>{title}</span>
        <div className="info">
          <p>Base Price: ₹{basePrice}</p>
          <p>Total Price: ₹{calculateTotalPrice}</p>
        </div>
        <button className="start-button">Start</button>
      </div>
    );
  }

  return (
    <div className="card-container">
      <CardSection className="card-image">
        <img src={image} alt={title} className="image" />
      </CardSection>

      <CardSection className="card-features">
        <h3>{title}</h3>
        <p>{details}</p>
      </CardSection>

      <CardSection className="card-add-ons">
        <h3>ADD ON</h3>
        <ul>
          {addOns.map((addOn, index) => (
            <li key={index}>
              <label>
                <input
                  type="checkbox"
                  onChange={(e) => handleAddOnChange(addOn, e.target.checked)}
                />
                {addOn.name} (+₹{addOn.price})
              </label>
            </li>
          ))}
        </ul>
      </CardSection>

      <CardSection className="card-summary">
        <h4>Summary</h4>
        <p>Base Price: ₹{basePrice}</p>
        <p>Add-Ons: ₹{Object.values(selectedAddOns).reduce((total, price) => total + price, 0)}</p>
        <p>Total Price: ₹{calculateTotalPrice}</p>
        <button className="done-button" onClick={toggleMinimize}>Done</button>
      </CardSection>
    </div>
  );
};

const CardSection = ({ className, children }) => {
  return <div className={`card ${className}`}>{children}</div>;
};

const App = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [priceRange, setPriceRange] = useState([1000, 5000]);
  const [productsData, setProductData] = useState([]);

  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data, error } = await supabase.from('products').select();
        if (error) throw error;

        const productPromises = data.map(async (product) => {
          const { data: image, error: imageError } = await supabase.storage
            .from('addon')
            .createSignedUrl(product.image, 3600);
          if (imageError) throw imageError;

          const { data: addons, error: addonsError } = await supabase
            .from('addons')
            .select()
            .eq('productid', product.id);
          if (addonsError) throw addonsError;

          const addonsWithUrls = await Promise.all(
            addons.map(async (addon) => {
              const { data: addonImage, error: addonImageError } = await supabase.storage
                .from('addon')
                .createSignedUrl(addon.image, 3600);
              if (addonImageError) throw addonImageError;
              return { ...addon, image: addonImage.signedUrl };
            })
          );

          return { ...product, image: image.signedUrl, addons: addonsWithUrls };
        });

        const products = await Promise.all(productPromises);
        setProductData(products);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    getProducts();
  }, []);

  const handleSearch = (event) => {
    setSearchQuery(event.target.value.toLowerCase());
  };

  const handleSliderChange = (event, newValue) => {
    setPriceRange(newValue);
  };

  return (
    <div className="App">
      <div className="search-filter">
        <input
          type="text"
          placeholder="Search products..."
          value={searchQuery}
          onChange={handleSearch}
          className="search-bar"
        />
        <Slider
          value={priceRange}
          onChange={handleSliderChange}
          valueLabelDisplay="auto"
          min={1000}
          max={5000}
          className="price-slider"
        />
      </div>

      <div className="products-grid">
        {productsData
          .filter((product) => {
            const price = parseInt(product.price, 10);
            return product.title.toLowerCase().includes(searchQuery) && price >= priceRange[0] && price <= priceRange[1];
          })
          .map((product, index) => (
            <Card
              key={index}
              title={product.title}
              price={product.price}
              details={product.details}
              addOns={product.addons}
              image={product.image}
              initialMinimized={product.initialMinimized}
            />
          ))}
      </div>

      <ProductForm />
    </div>
  );
};

export default App;
