import React, { useState, useMemo } from 'react';
import './boq.css';
import { Slider, Modal, TextField, Button } from '@mui/material';

const Card = ({ title, price, details, addOns, initialMinimized = false }) => {
  const [selectedAddOns, setSelectedAddOns] = useState({});
  const [isMinimized, setIsMinimized] = useState(initialMinimized);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newAddOn, setNewAddOn] = useState({ name: '', price: '' });
  const [localAddOns, setLocalAddOns] = useState(addOns);

  const basePrice = useMemo(() => parseInt(price.replace('₹', ''), 10), [price]);

  const handleAddOnChange = (addOn, isChecked) => {
    setSelectedAddOns((prevSelectedAddOns) => ({
      ...prevSelectedAddOns,
      [addOn.name]: isChecked ? addOn.price : 0,
    }));
  };

  const calculateTotalPrice = useMemo(() => {
    return Object.values(selectedAddOns).reduce((total, addOnPrice) => total + addOnPrice, basePrice);
  }, [selectedAddOns, basePrice]);

  const toggleMinimize = () => setIsMinimized((prev) => !prev);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  const handleNewAddOnChange = (e) => {
    const { name, value } = e.target;
    setNewAddOn((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddNewAddOn = () => {
    if (newAddOn.name && newAddOn.price) {
      setLocalAddOns([...localAddOns, { name: newAddOn.name, price: parseInt(newAddOn.price, 10) }]);
      setNewAddOn({ name: '', price: '' });
      closeModal();
    }
  };

  if (isMinimized) {
    return (
      <div className="minimized-card" onClick={toggleMinimize}>
        <span>{title}</span>
        <div className="info">
          <p>Base Price: ₹{basePrice}</p>
          <p>Total Price: ₹{calculateTotalPrice}</p>
        </div>
        <button className="start-button">Start</button>
      </div>
    );
  }

  return (
    <>
      <div className="card-container">
        <CardSection className="card-image">
          <img src="https://placehold.co/120x120" alt={title} className="image" />
        </CardSection>

        <CardSection className="card-features">
          <h3>{title}</h3>
          <p>{details}</p>
          <button className="edit" onClick={openModal}>Edit</button>
        </CardSection>

        <CardSection className="card-add-ons">
          <button className="add-on">ADD ON +</button>
          <ul>
            {localAddOns.map((addOn, index) => (
              <li key={index}>
                <label>
                  <input
                    type="checkbox"
                    onChange={(e) => handleAddOnChange(addOn, e.target.checked)}
                  />
                  {addOn.name} (+₹{addOn.price})
                </label>
              </li>
            ))}
          </ul>
        </CardSection>

        <CardSection className="card-summary">
          <h4>Summary</h4>
          <p>Base Price: ₹{basePrice}</p>
          <p>Add-Ons: ₹{Object.values(selectedAddOns).reduce((total, price) => total + price, 0)}</p>
          <p>Total Price: ₹{calculateTotalPrice}</p>
          <button className="done-button" onClick={toggleMinimize}>Done</button>
        </CardSection>
      </div>

      <Modal open={isModalOpen} onClose={closeModal}>
        <div className="modal-content">
          <h3>Add New Add-On</h3>
          <TextField
            label="Add-On Name"
            name="name"
            value={newAddOn.name}
            onChange={handleNewAddOnChange}
            fullWidth
          />
          <TextField
            label="Add-On Price"
            name="price"
            value={newAddOn.price}
            onChange={handleNewAddOnChange}
            fullWidth
            type="number"
          />
          <Button variant="contained" color="primary" onClick={handleAddNewAddOn}>
            Add Add-On
          </Button>
        </div>
      </Modal>
    </>
  );
};

const CardSection = ({ className, children }) => {
  return <div className={`card ${className}`}>{children}</div>;
};

const App = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [priceRange, setPriceRange] = useState([1000, 5000]);
  const [compareList, setCompareList] = useState([]);

  const productsData = useMemo(() => [
    {
      title: "Workstation",
      price: "₹2000",
      details: "A functional workstation for your needs.",
      image: "https://placehold.co/120x120/workstation.jpg", // example image
      addOns: [
        { name: "Storage", price: 500 },
        { name: "Softboards", price: 200 },
        { name: "Plant Dividers", price: 100 },
        { name: "Raceway", price: 50 },
        { name: "Side Covers", price: 150 }
      ],
      initialMinimized: false
    },
    {
      title: "Conference Table",
      price: "₹4000",
      details: "A large table suitable for meetings.",
      image: "https://placehold.co/120x120/conference-table.jpg",
      addOns: [
        { name: "Power Outlets", price: 600 },
        { name: "Cable Management", price: 200 },
        { name: "Wireless Charging", price: 300 }
      ],
      initialMinimized: true
    }
  ], []);

  const handleSearch = (event) => {
    setSearchQuery(event.target.value.toLowerCase());
  };

  const handleSliderChange = (event, newValue) => {
    setPriceRange(newValue);
  };

  const filteredProducts = productsData.filter((product) => {
    const price = parseInt(product.price.replace('₹', ''), 10);
    return (
      product.title.toLowerCase().includes(searchQuery) &&
      price >= priceRange[0] &&
      price <= priceRange[1]
    );
  });

  return (
    <div className="App">
      <div className="search-filter">
        <input
          type="text"
          placeholder="Search products..."
          value={searchQuery}
          onChange={handleSearch}
          className="search-bar"
        />
        <Slider
          value={priceRange}
          onChange={handleSliderChange}
          valueLabelDisplay="auto"
          min={1000}
          max={5000}
          className="price-slider"
        />
      </div>

      <div className="products-grid">
        {filteredProducts.map((product, index) => (
          <div key={index}>
            <Card
              title={product.title}
              price={product.price}
              details={product.details}
              addOns={product.addOns}
              initialMinimized={product.initialMinimized}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
