import React, { useState, useRef, useEffect } from 'react';
import './App.css';

function Card({ title, price, details, addOns }) {
  const [isDropdownVisible, setDropdownVisible] = useState(false);
  const [selectedAddOns, setSelectedAddOns] = useState({});
  const dropdownRef = useRef(null);
  const basePrice = parseInt(price.replace('₹', ''), 10);

  const toggleDropdown = () => {
    setDropdownVisible(!isDropdownVisible);
  };

  const handleAddOnChange = (addOn, isChecked) => {
    setSelectedAddOns((prevSelectedAddOns) => ({
      ...prevSelectedAddOns,
      [addOn.name]: isChecked ? addOn.price : 0,
    }));
  };

  const calculateTotalPrice = () => {
    return Object.values(selectedAddOns).reduce((total, addOnPrice) => total + addOnPrice, basePrice);
  };

  const handleClickOutside = (event) => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      setDropdownVisible(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const clearAddOns = () => {
    setSelectedAddOns({});
  };

  return (
    <div className="card">
      <div className="image-container">
        <img src="https://placehold.co/120x120" alt={title} className="image" />
        <div>
          <h3>{title}</h3>
          <div className="details">
            <span className="price text-red">₹{calculateTotalPrice()}</span>
            <div>
              <button className="edit">Edit</button>
              <button className="see-more">See More</button>
            </div>
          </div>
        </div>
      </div>

      <div className="add-on-container" ref={dropdownRef}>
        <button className="add-on" onClick={toggleDropdown}>ADD ON +</button>
        {isDropdownVisible && (
          <div className="dropdown-menu">
            <ul>
              {addOns.map((addOn, index) => (
                <li key={index} className={`addon-item ${selectedAddOns[addOn.name] ? 'selected' : ''}`}>
                  <label>
                    <input
                      type="checkbox"
                      checked={!!selectedAddOns[addOn.name]}
                      onChange={(e) => handleAddOnChange(addOn, e.target.checked)}
                    />
                    <img src={addOn.image} alt={addOn.name} className="addon-image" />
                    {addOn.name} (+₹{addOn.price})
                  </label>
                </li>
              ))}
            </ul>
            <button className="clear-add-ons" onClick={clearAddOns}>Clear All Add-Ons</button>
          </div>
        )}
        <div className="add-on-details">
          <span>{details}</span>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [filter, setFilter] = useState('all');

  const furniture = [
    {
      title: 'Table',
      price: '₹4500',
      details: '4 legs, 5 tops',
      addOns: [
        { name: 'Storage', price: 1000, image: 'https://placehold.co/50x50?text=Storage' },
        { name: 'Leg Guard', price: 500, image: 'https://placehold.co/50x50?text=Leg+Guard' }
      ]
    },
    // ... other furniture items
  ];

  const chairs = [
    {
      title: 'Executive Chair',
      price: '₹7000',
      details: 'High back, leather',
      addOns: [
        { name: 'Cushion', price: 500, image: 'https://placehold.co/50x50?text=Custom+Shelves' },
        { name: 'Armrest Pads', price: 300, image: 'https://placehold.co/50x50?text=Custom+Shelves' }
      ]
    },
    // ... other chair items
  ];

  const partitions = [
    {
      title: 'Glass Partition',
      price: '₹10000',
      details: 'Tempered glass, frameless',
      addOns: [
        { name: 'Frosted Glass', price: 2000, image: 'https://placehold.co/50x50?text=Custom+Shelves' },
        { name: 'Custom Tint', price: 1500, image: 'https://placehold.co/50x50?text=Custom+Shelves' }
      ]
    },
    // ... other partition items
  ];

  const getFilteredProducts = () => {
    switch (filter) {
      case 'furniture':
        return furniture;
      case 'chairs':
        return chairs;
      case 'partitions':
        return partitions;
      default:
        return [...furniture, ...chairs, ...partitions];
    }
  };

  return (
    <>
      <header>
        <h1>Work Better, Together</h1>
        <p>Be surrounded by inspiration</p>
      </header>

      <div className="filter-container">
        <button onClick={() => setFilter('all')}>All</button>
        <button onClick={() => setFilter('furniture')}>Furniture</button>
        <button onClick={() => setFilter('chairs')}>Chairs</button>
        <button onClick={() => setFilter('partitions')}>Partitions</button>
      </div>

      <div className="container">
        {getFilteredProducts().map((product, index) => (
          <Card
            key={index}
            title={product.title}
            price={product.price}
            details={product.details}
            addOns={product.addOns}
          />
        ))}
      </div>

      <div className="center-button-container">
        <button className="explore-more">EXPLORE MORE</button>
      </div>
    </>
  );
}

export default App;
