import React, { useState } from 'react';
import './form.css';
import { useForm, useFieldArray } from 'react-hook-form';
import { supabase } from './supabase';

const ProductForm = () => {
  const categories = [
    { name: 'Furniture', subcategories: ['Linear Workstation', 'L-Type Workstation', 'Md cabin','Manager Cabin','Small Cabin','Discussion Room','Interview Room','conference room','Board Room','Meeting Room','HR room','Video Recording Room','Reception','pantry','Phone Booth','Break Out room','UPS','Bms','Executive Washroom'], subcategories1: ['Option1', 'Option2'] },
    { name: 'Civil / Plumbing', subcategories: ['Basic','Luxury','Premium'], subcategories1: ['Option1', 'Option2'] },
    { name: 'Lighting', subcategories: ['Workstation', 'Cabin', 'meeting room', 'Public Spaces'], subcategories1: ['Option1', 'Option2'] },
    { name: 'Electrical', subcategories: ['Per sq ft cost standard'], subcategories1: ['Option1', 'Option2'] },
    { name: 'Partitions- door / windows / ceilings', subcategories: ['Workstation', 'Cabin', 'meeting room'], subcategories1: ['Option1', 'Option2'] },
    { name: 'Paint', subcategories: [], subcategories1: [] },
    { name: 'HVAC', subcategories: ['Full Centralized'], subcategories1: ['Option1', 'Option2'] },
    { name: 'Smart Solutions', subcategories: [], subcategories1: [] },
    { name: 'Flooring', subcategories: ['Workstation', 'Cabin', 'meeting room', 'Public Spaces'], subcategories1: ['Option1', 'Option2'] },
    { name: 'Accessories', subcategories: [], subcategories1: [] }
  ];

  const { register, handleSubmit, control, formState: { errors } } = useForm({
    defaultValues: {
      title: '',
      details: '',
      price: '',
      image: null,
      category: '',
      subcategory: '',
      subcategory1: '',
      addons: [{ image: null, title: '', price: '' }]
    }
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: 'addons'
  });

  const [subcategories, setSubcategories] = useState([]);
  const [subcategories1, setSubcategories1] = useState([]);

  const onSubmit = async (data) => {
    console.log(data);
    const { data: ProductImage, error: ProductImageError } = await supabase.storage.from("addon").upload(`${data.title}-${data.details}`, data.image[0]);
    if (ProductImageError) {
      console.log(ProductImageError);
      return;
    }

    const { data: Product, error } = await supabase.from("products").insert({
      title: data.title,
      details: data.details,
      price: data.price,
      image: ProductImage.path,
      category: data.category,
      subcategory: data.subcategory || null, // Save the subcategory if present
      subcategory1: data.subcategory1 || null, // Save the subcategory1 if present
    }).select().single();

    if (error) {
      console.log(error);
      return;
    }

    for (let addons in data.addons) {
      const adf = data.addons[addons];
      console.log(adf);
      const { data: AddonFile, error: AddonFileError } = await supabase.storage.from("addon").upload(`${Product.id}-${adf.title}`, adf.image[0]);
      if (AddonFileError) {
        console.error(AddonFileError);
        await supabase.from("products").delete().eq("id", Product.id);
        break;
      }
      const { error: AddonError } = await supabase.from("addons").insert({
        title: adf.title,
        price: adf.price,
        image: AddonFile.path,
        productid: Product.id
      });
      if (AddonError) {
        console.error(AddonError);
        await supabase.from("products").delete().eq("id", Product.id);
        break;
      }
    }

    // Refresh the page after submission
    window.location.reload();
  };

  return (
    <form className="" onSubmit={handleSubmit(onSubmit)}>
      <div>
        <label>Category:</label>
        <select 
          {...register('category', { required: 'Category is required' })}
          onChange={(e) => {
            const category = e.target.value;
            const selectedCatObj = categories.find(cat => cat.name === category);
            setSubcategories(selectedCatObj?.subcategories || []);
            setSubcategories1(selectedCatObj?.subcategories1 || []);
          }}
        >
          <option value="">Select Category</option>
          {categories.map((category, index) => (
            <option key={index} value={category.name}>
              {category.name}
            </option>
          ))}
        </select>
        {errors.category && <p>{errors.category.message}</p>}
      </div>

      {/* Conditionally render subcategories for selected categories */}
      {subcategories.length > 0 && (
        <div>
          <label>Subcategory:</label>
          <select {...register('subcategory', { required: 'Subcategory is required' })}>
            <option value="">Select Subcategory</option>
            {subcategories.map((subcategory, index) => (
              <option key={index} value={subcategory}>
                {subcategory}
              </option>
            ))}
          </select>
          {errors.subcategory && <p>{errors.subcategory.message}</p>}
        </div>
      )}

      {/* Conditionally render subcategories1 for selected categories */}
      {subcategories1.length > 0 && (
        <div>
          <label>Subcategory1:</label>
          <select {...register('subcategory1', { required: 'Subcategory1 is required' })}>
            <option value="">Select Subcategory1</option>
            {subcategories1.map((subcategory1, index) => (
              <option key={index} value={subcategory1}>
                {subcategory1}
              </option>
            ))}
          </select>
          {errors.subcategory1 && <p>{errors.subcategory1.message}</p>}
        </div>
      )}

      <div>
        <label>Title:</label>
        <input
          type="text"
          {...register('title', { required: 'Title is required' })}
        />
        {errors.title && <p>{errors.title.message}</p>}
      </div>

      <div>
        <label>Details:</label>
        <textarea
          {...register('details', { required: 'Details are required' })}
        />
        {errors.details && <p>{errors.details.message}</p>}
      </div>

      <div>
        <label>Price:</label>
        <input
          type="number"
          {...register('price', { required: 'Price is required' })}
        />
        {errors.price && <p>{errors.price.message}</p>}
      </div>

      <div>
        <label>Image:</label>
        <input
          type="file"
          {...register(`image`, { required: 'Image is required' })}
        />
      </div>

      <div>
        <h3>Add-ons</h3>
        {fields.map((addon, index) => (
          <div key={addon.id}>
            <label>Image:</label>
            <input
              type="file"
              {...register(`addons.${index}.image`, { required: 'Image is required' })}
            />
            {errors.addons?.[index]?.image && <p>{errors.addons[index].image.message}</p>}

            <label>Title:</label>
            <input
              type="text"
              {...register(`addons.${index}.title`, { required: 'Addon title is required' })}
            />
            {errors.addons?.[index]?.title && <p>{errors.addons[index].title.message}</p>}

            <label>Price:</label>
            <input
              type="number"
              {...register(`addons.${index}.price`, { required: 'Addon price is required' })}
            />
            {errors.addons?.[index]?.price && <p>{errors.addons[index].price.message}</p>}

            <button type="button" onClick={() => remove(index)}>
              Remove Addon
            </button>
          </div>
        ))}

        <button
          type="button"
          onClick={() => append({ image: null, title: '', price: '' })}
        >
          Add Addon
        </button>
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default ProductForm;
