import React, { useState } from 'react';
import './form.css';
import { useForm, useFieldArray } from 'react-hook-form';
import { supabase } from './supabase';

const ProductForm = () => {
  const categories = [
    { name: 'Furniture', subcategories: ['Chairs', 'Tables', 'Desks'] },
    { name: 'Civil / Plumbing', subcategories: [] },
    { name: 'Lighting', subcategories: ['Ceiling Lights', 'Lamps', 'Wall Lights'] },
    { name: 'Electrical', subcategories: [] },
    { name: 'Partitions- door / windows / ceilings', subcategories: [] },
    { name: 'Paint', subcategories: [] },
    { name: 'HVAC', subcategories: [] },
    { name: 'Smart Solutions', subcategories: [] },
    { 
      name: 'Flooring', 
      subcategories: [
        { name: 'Workstation', options: ['pantry', 'reception'] },
        { name: 'Cabin', options: ['Current status'] },
        { name: 'CR/ MR', options: ['Bare', 'Basic Tiling is done'] },
        { name: 'Public Spaces', options: ['150 Vinyl', 'Carpet', 'Laminate', 'Ceramic', 'Polish concrete'] }
      ]
    },
    { name: 'Accessories', subcategories: [] }
  ];

  const { register, handleSubmit, control, formState: { errors } } = useForm({
    defaultValues: {
      title: '',
      details: '',
      price: '',
      image: null,
      category: '',
      subcategory: '',
      addons: [{ image: null, title: '', price: '' }]
    }
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: 'addons'
  });

  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedSubcategory, setSelectedSubcategory] = useState('');

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setSelectedSubcategory('');
  };

  const handleSubcategoryChange = (e) => {
    setSelectedSubcategory(e.target.value);
  };

  const getSubcategoryOptions = () => {
    const category = categories.find(cat => cat.name === selectedCategory);
    if (category && category.subcategories.length > 0) {
      return category.subcategories;
    }
    return [];
  };

  const getOptionsForSubcategory = () => {
    const subcategories = getSubcategoryOptions();
    const subcategory = subcategories.find(sub => sub.name === selectedSubcategory);
    if (subcategory && subcategory.options) {
      return subcategory.options;
    }
    return [];
  };

  return (
    <form onSubmit={handleSubmit(data => console.log(data))}>
      <select {...register('category')} onChange={handleCategoryChange}>
        <option value="">Select Category</option>
        {categories.map(cat => (
          <option key={cat.name} value={cat.name}>{cat.name}</option>
        ))}
      </select>

      {selectedCategory && (
        <select {...register('subcategory')} onChange={handleSubcategoryChange}>
          <option value="">Select Subcategory</option>
          {getSubcategoryOptions().map(sub => (
            <option key={sub.name} value={sub.name}>{sub.name}</option>
          ))}
        </select>
      )}

      {selectedSubcategory && getOptionsForSubcategory().length > 0 && (
        <select {...register('options')}>
          <option value="">Select Option</option>
          {getOptionsForSubcategory().map(option => (
            <option key={option} value={option}>{option}</option>
          ))}
        </select>
      )}

      {/* Other form fields */}
      <button type="submit">Submit</button>
    </form>
  );
};

export default ProductForm;
