import React from 'react';
import { useForm, useFieldArray } from 'react-hook-form';
import { createClient} from "@supabase/supabase-js";
const supabase = createClient('https://iultciezzujrjhcjjmkg.supabase.co', 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Iml1bHRjaWV6enVqcmpoY2pqbWtnIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MjY4MjkzMTYsImV4cCI6MjA0MjQwNTMxNn0.9SpSxdPMQEy4nguFLElw7wlq0RNscmT-tOK0Qs_UGyk');

const ProductForm = () => {
  const { register, handleSubmit, control, formState: { errors } } = useForm({
    defaultValues: {
      title: '',
      details: '',
      price: '',
      addons: [{ image: null, title: '', price: '' }]
    }
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: 'addons'
  });

  const onSubmit = async (data) => {
    
    const {data:Product, error} = await supabase.from("products").insert({
        title:data.title,
        details:data.details,
        price:data.price,
    }).select().single();
    console.log(error)
    for(let addons in data.addons){
        const {data:AddonFile, error:AddonFileError} = await supabase.storage.from("addons").upload(`${Product.id}-${data.addons[addons]}`,data.addons[addons].image);
        if(AddonFileError){
            console.error(AddonFileError);
            await supabase.from("products").delete().eq("id",Product.id);
        }
        const { error:AddonError} = await supabase.from("addons").insert({title:addons.title, price:addons.price, image:AddonFile.path, productid:Product.id});
        if(AddonError){
            console.error(AddonError);
        }
        
    }
  };

  return (
    <form className="" onSubmit={handleSubmit(onSubmit)}>
      <div>
        <label>Title:</label>
        <input
          type="text"
          {...register('title', { required: 'Title is required' })}
        />
        {errors.title && <p>{errors.title.message}</p>}
      </div>

      <div>
        <label>Details:</label>
        <textarea
          {...register('details', { required: 'Details are required' })}
        />
        {errors.details && <p>{errors.details.message}</p>}
      </div>

      <div>
        <label>Price:</label>
        <input
          type="number"
          {...register('price', { required: 'Price is required' })}
        />
        {errors.price && <p>{errors.price.message}</p>}
      </div>

      <div>
        <h3>Add-ons</h3>
        {fields.map((addon, index) => (
          <div key={addon.id}>
            <label>Image:</label>
            <input
              type="file"
              {...register(`addons.${index}.image`, { required: 'Image is required' })}
            />
            {errors.addons?.[index]?.image && <p>{errors.addons[index].image.message}</p>}

            <label>Title:</label>
            <input
              type="text"
              {...register(`addons.${index}.title`, { required: 'Addon title is required' })}
            />
            {errors.addons?.[index]?.title && <p>{errors.addons[index].title.message}</p>}

            <label>Price:</label>
            <input
              type="number"
              {...register(`addons.${index}.price`, { required: 'Addon price is required' })}
            />
            {errors.addons?.[index]?.price && <p>{errors.addons[index].price.message}</p>}

            <button type="button" onClick={() => remove(index)}>
              Remove Addon
            </button>
          </div>
        ))}

        <button
          type="button"
          onClick={() => append({ image: null, title: '', price: '' })}
        >
          Add Addon
        </button>
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default ProductForm;
