import React, { useState } from 'react';
import './App.css';

function Card({ title, price, details, addOns }) {
  const [selectedAddOns, setSelectedAddOns] = useState({});
  const [isMinimized, setIsMinimized] = useState(false);
  const basePrice = parseInt(price.replace('₹', ''), 10);

  const handleAddOnChange = (addOn, isChecked) => {
    setSelectedAddOns({
      ...selectedAddOns,
      [addOn.name]: isChecked ? addOn.price : 0,
    });
  };

  const calculateTotalPrice = () => {
    return Object.values(selectedAddOns).reduce((total, addOnPrice) => total + addOnPrice, basePrice);
  };

  const toggleMinimize = () => {
    setIsMinimized(!isMinimized);
  };

  if (isMinimized) {
    return (
      <div className="minimized-card">
        <span>{title}</span>
        <button className="start-button" onClick={toggleMinimize}>Start</button>
      </div>
    );
  }

  return (
    <div className="card-container">
      <div className="card card-image">
        <img src="https://placehold.co/120x120" alt={title} className="image" />
      </div>

      <div className="card card-features">
        <h3>{title}</h3>
        <p>{details}</p>
        <button className="edit">Edit</button>
      </div>

      <div className="card card-add-ons">
        <button className="add-on">ADD ON +</button>
        <ul>
          {addOns.map((addOn, index) => (
            <li key={index}>
              <label>
                <input
                  type="checkbox"
                  onChange={(e) => handleAddOnChange(addOn, e.target.checked)}
                />
                {addOn.name} (+₹{addOn.price})
              </label>
            </li>
          ))}
        </ul>
      </div>

      <div className="card card-summary">
        <h4>Summary</h4>
        <p>Base Price: ₹{basePrice}</p>
        <p>Add-Ons: ₹{Object.values(selectedAddOns).reduce((total, price) => total + price, 0)}</p>
        <p>Total Price: ₹{calculateTotalPrice()}</p>
        <button className="done-button" onClick={toggleMinimize}>Done</button>
      </div>
    </div>
  );
}

export default Card;
